// This gets TLE

class Solution {
 public:
  int minCost(std::vector<int>& houses, std::vector<std::vector<int>>& cost, int m, int n, int target) {
    int currentTarget = 1;
    for (int i = 1; i < houses.size(); ++i)
      if (houses[i] != houses.at(i - 1) && houses[i] != 0 && houses[i - 1] != 0) ++currentTarget;

    int currentCost = 0;
    int solution = INT_MAX;
    std::vector<int> emptyPositions;
    for (int i = 0; i < houses.size(); ++i)
      if (houses[i] == 0) emptyPositions.push_back(i);

    int currentEmptyPositionIndex = -1;

    // std::cout << "Empty positions: ";
    // std::copy(emptyPositions.begin(), emptyPositions.end(), std::ostream_iterator<int>(std::cout, " "));
    // std::cout << '\n';
    // std::cout << "Starting target: " << currentTarget << '\n';
    minCost(houses, cost, emptyPositions, currentEmptyPositionIndex, n, target, currentTarget, currentCost, solution);

    // print solutions
    // std::copy(solutions.begin(), solutions.end(), std::ostream_iterator<int>(std::cout, " "));
    // std::cout << '\n';
    if (solution == INT_MAX) return -1;
    else return solution;
  }

 private:
  void minCost(std::vector<int>& houses, const std::vector<std::vector<int>>& cost,
               const std::vector<int>& emptyPositions, int currentEmptyPositionIndex, const int& n, const int& target,
               const int& currentTarget, const int& currentCost, int& solution) {
    ++currentEmptyPositionIndex;
    // print currentEmptyPositionIndex
    std::cout << "currentEmptyPositionIndex: " << currentEmptyPositionIndex << '\n';
    std::cout << "Cost: " << currentCost << " target: " << currentTarget << " houses: ";
    std::copy(houses.begin(), houses.end(), std::ostream_iterator<int>(std::cout, " "));
    std::cout << '\n';
    if (currentTarget > target) return;
    if (currentCost > solution) return;
    if (currentEmptyPositionIndex == emptyPositions.size()) {
      if (currentTarget == target) {
        solution = std::min(solution, currentCost);
        std::cout << "pushed: " << currentCost << '\n';
      }
      return;
    }

    const int currentEmptyPosition = emptyPositions[currentEmptyPositionIndex];
    // std::cout << "Current empty position: " << currentEmptyPosition << '\n';
    for (int i = 1; i <= n; ++i) {
      int newTarget = currentTarget;
      if ((currentEmptyPosition > 0 && houses.at(currentEmptyPosition - 1) != i)) ++newTarget;
      if ((currentEmptyPosition < houses.size() - 1 && houses[currentEmptyPosition + 1] != 0 &&
           houses[currentEmptyPosition + 1] != i))
        ++newTarget;
      int newCost = currentCost + cost[currentEmptyPosition].at(i - 1);
      houses[currentEmptyPosition] = i;
      minCost(houses, cost, emptyPositions, currentEmptyPositionIndex, n, target, newTarget, newCost, solution);
    }

    houses[currentEmptyPosition] = 0;
  }
};

